mapper.update:
UPDATE wait_list
SET wait_ranking = #{waitRanking}
WHERE book_id = #{bookId} AND wait_user = #{waitUser}

mapper.select:
SELECT 
FROM wait_list
WHERE wait_user = #{waitUser} AND book_id = #{bookId}

SELECT COUNT(*)
FROM wait_list
WHERE book_id = #{bookId}

SELECT COUNT(*)
FROM wait_list
WHERE wait_user = #{waitUser}


SELECT title, rank
FROM(
	SELECT b_title title, w_rk rank, rownum rnum
	FROM(
			SELECT b.title b_title,  w.rk w_rk
			FROM (
				SELECT book_id, wait_user,
				RANK() OVER (PARTITION BY book_id ORDER BY wait_ranking DESC ) as rk 
				FROM wait_list) w, book b
			WHERE w.book_id = b.book_id(+) AND wait_user = #{userId}
			ORDER BY b.title
	)
	WHERE rownum <= #{endIndex}
)
WHERE rnum >= #{startIndex}
